import axios from 'axios';
import React, { useState } from 'react'

function UserLoginPage() {
    const [user,setUser]= useState({username:"",password:""});

    const handleUsername= (event) => {
        setUser({...user,
            username:event.target.value});
    }  
    const handlePassword= (event) => {
        setUser({...user,
            password:event.target.value});
    }  

    const handleButton = (event) => {

        event.preventDefault();
        console.log(user);
       
        axios.post("http://localhost:9191/a-service/v1/users/login" , user)
            .then(response => {
            //localStorage.setItem('token', response.data.message);
            //localStorage.setItem('currentUser', response.data.id);
            //localStorage.setItem('username',response.data.username);
            //console.log(localStorage.getItem('token'));
            //console.log(localStorage.getItem('currentUser'));
            //window.axios.defaults.headers.common['Authorization'] = localStorage.getItem('token');
            })     
    }

    return (
        <div className='container'>
            <form>
                <h1 className='text-center'>Login</h1>
                <div className='form-group'>
                    <label>Username</label>
                    <input className='form-control' onChange = {(i) => handleUsername(i)} />
                </div>
                <div>
                    <label>Password</label>
                    <input className='form-control' type='password' onChange = {(i) => handlePassword(i)}/>
                </div>
                <button className='btn btn-primary' type='submit'  onClick={handleButton}>Login</button>
            </form>
        </div>
    )
}

export default UserLoginPage
