import { useState } from "react";
import { useEffect } from 'react';

function Home(props) {
    const [accounts, setAccounts] = useState([]);
    const [userId]=useState(localStorage.getItem("currentUser"));
    const [transactions, setTransactions] = useState([]);
    
    const getTransactionList = (id) => {
        fetch(`/v1/transactions/${accountType}/${userId}`)
            .then(res => res.json())
            .then(
                (result) => {
                    setTransactions(result)
                },
                (error) => {
                    console.log(error)
                }
            )
    }

    useEffect(() => {
        fetch(`/a-service/v1/accounts/getAllAccount/${userId}`,{
            method : "GET",
            headers: {
                "Content-Type": "application/json",
                "Authorization" : localStorage.getItem("token"),
              },
        })
            .then(res => res.json())
            .then(
                (result) => {
                    setAccounts(result)
                },
                (error) => {
                    console.log(error)
                }
            )
    }, [])
    return (
        <div className="container">
            <table className="table">
                <thead className="thead-dark">
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">Account</th>
                        <th scope="col">Balance</th>
                    </tr>
                </thead>
                <tbody>
                    {accounts.map(account =>
                        <tr>
                            <th>{account.id}</th>
                            <td>{account.accountType}</td>
                            <td>{account.balance}</td>
                            <IconButton
                        aria-label="expand row"
                        size="small"
                        onClick={() => { setOpen(!open); getTransactionList(account.accountType); }}
                    >
                        {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                    </IconButton>
                        </tr>
                    )
                    }
                </tbody>
            </table>
        </div>
    );
}
export default Home;
