import { useState } from "react";
import { useEffect } from 'react';
import axios from "axios";

function ExchangeComponent() {
    const [soldAccountType, setSoldAccountType] = useState();
    const [receivedAccountType, setReceivedAccountType] = useState();
    const [soldAmount, setSoldAmount] = useState();
    const [exchangeRate, setExchangeRate] = useState();
    const [userId, setUserId] = useState();

  /*  const handleSoldAccountType = (value) => {
        setSoldAccountType(value);
        setUserId(1);
    }
    const handleReceivedAccountType = (value) => {
        setReceivedAccountType(value);
    }
    const handleSoldAmount = (value) => {
        setSoldAmount(value);
    }
    const handleExchangeRate = (value) => {
        setexchangeRate(value);
    }
*/
    const onClickExchange = event => {
        event.preventDefault();
        setUserId("1");
        const body ={
            soldAccountType,
            receivedAccountType,
            soldAmount,
            exchangeRate,
            userId
        }
        axios.post("/v1/transactions/",body)
        .then(response =>{
           
        } )
        .catch(err => console.log(err))
    }
    return (
        <div className="container">
            <form>
                    <h1 className='text-center'>Exchange</h1>
                    <div className='form-group'>
                        <label>Sold Account Type</label>
                        <input className='form-control' name="soldAccountType" onChange={event=>setSoldAccountType(event.target.value)}/>
                    </div>
                    <div className='form-group'>
                        <label>Received Account Type</label>
                        <input className='form-control' name="receivedAccountType" onChange={event=>setReceivedAccountType(event.target.value)}/>
                    </div>
                    <div className='form-group'>
                        <label>Sold Amount</label>
                        <input className='form-control' name="soldAmount" onChange={event=>setSoldAmount(event.target.value)}/>
                    </div>
                    <div className='form-group'>
                        <label>Exchange Rate</label>
                        <input className='form-control' name="exchangeRate" onChange={event=>setExchangeRate(event.target.value)}/>
                    </div>
                    <button className='btn btn-primary' onClick={onClickExchange}>Exchange</button>

                </form>
        </div>
    );
}
export default ExchangeComponent;
