import axios from 'axios';
import React, { useState } from 'react'

function UserLoginPage() {
    const [user,setUser]= useState();
    const [username,setUsername] = useState("");
    const [password,setPassword] = useState("");  

    const handleUsername = (value) => {
        setUsername(value)
    } 

    const handlePassword = (value) => {
        setPassword(value)
    }

   /* const handleButton = (event) => {

        event.preventDefault();
        console.log(user);
       
        axios.post("http://localhost:9191/a-service/v1/users/login" , user)
            .then(response => {
            //localStorage.setItem('token', response.data.message);
            //localStorage.setItem('currentUser', response.data.id);
            //localStorage.setItem('username',response.data.username);
            //console.log(localStorage.getItem('token'));
            //console.log(localStorage.getItem('currentUser'));
            //window.axios.defaults.headers.common['Authorization'] = localStorage.getItem('token');
            })     
    }
    */
    function login(e) {     
 
        axios.post("/a-service/v1/users/login" , {
            username:username,
            password:password
        })
        .then((res)=>res.json)
        .then((res)=> console.log(res.data))
        .catch((err) => console.log(err))
    }
    return (
        <div className='container'>
            <form>
                <h1 className='text-center'>Login</h1>
                <div className='form-group'>
                    <label>Username</label>
                    <input className='form-control'  onChange = {(i) => handleUsername(i.target.value)} />
                </div>
                <div>
                    <label>Password</label>
                    <input className='form-control'  onChange = {(i) => handlePassword(i.target.value)}/>
                </div>
                <button className='btn btn-primary' type='submit'  onClick={() => login()}>Login</button>
            </form>
        </div>
    )
}

export default UserLoginPage
