import React from 'react';
import axios from 'axios';


class UserLoginPage extends React.Component {

    state = {
        username:null,
        password:null
    }
    onChange = event =>{
        const {name,value}=event.target;
        this.setState({
            [name]:value
        })
    }

    onClickLogin = event =>{
        event.preventDefault();

        const body = {
            username : this.state.username,
            password:this.state.password
        }
        axios.post('/a-service/v1/users/login',body)
        .then(response =>{
           // localStorage.setItem('token', response.data.message);
           // localStorage.setItem('currentUser', response.data.id);
          //  localStorage.setItem('username',response.data.username);
           // console.log(localStorage.getItem('token'));
           // console.log(localStorage.getItem('currentUser'));
          //  window.axios.defaults.headers.common['Authorization'] = localStorage.getItem('token');
           // window.location.assign('/');
        } )
        .catch(err => console.log(err))
    }
    
    render() {
        return (
            
            <div className='container'>
                <form>
                    <h1 className='text-center'>Login</h1>
                    <div className='form-group'>
                        <label>Username</label>
                        <input className='form-control' name="username" onChange={this.onChange}/>
                    </div>
                    <div>
                        <label>Password</label>
                        <input className='form-control' name="password" type="password" onChange={this.onChange}/>
                    </div>
                    <button className='btn btn-primary' onClick={this.onClickLogin}>Login</button>

                </form>
            </div>
        );
    }
}
export default UserLoginPage;
