import { useState } from "react";
import { useEffect } from 'react';
import axios from "axios";

function ExchangeComponent() {
    const [soldAccountType, setSoldAccountType] = useState("");
    const [receivedAccountType, setReceivedAccountType] = useState("");
    const [soldAmount, setSoldAmount] = useState("");
    const [exchangeRate, setexchangeRate] = useState("");
    const [userId, setUserId] = useState("");

    const handleSoldAccountType = (value) => {
        setSoldAccountType(value);
        setUserId(1);
    }
    const handleReceivedAccountType = (value) => {
        setReceivedAccountType(value);
    }
    const handleSoldAmount = (value) => {
        setSoldAmount(value);
    }
    const handleExchangeRate = (value) => {
        setexchangeRate(value);
    }

    const onClickExchange = () => {
        
        axios.post("/v1/transactions",{
            soldAccountType :soldAccountType,
            receivedAccountType:receivedAccountType,
            soldAmount:soldAmount,
            exchangeRate:exchangeRate,
            userId:userId
        })
        .then(response =>{
           
        } )
        .catch(err => console.log(err))
    }
    return (
        <div className="container">
            <form>
                    <h1 className='text-center'>Exchange</h1>
                    <div className='form-group'>
                        <label>Sold Account Type</label>
                        <input className='form-control' name="soldAccountType" onChange={(i)=>handleSoldAccountType(i.target.value)}/>
                    </div>
                    <div className='form-group'>
                        <label>Received Account Type</label>
                        <input className='form-control' name="receivedAccountType" onChange={(i)=>handleReceivedAccountType(i.target.value)}/>
                    </div>
                    <div className='form-group'>
                        <label>Sold Amount</label>
                        <input className='form-control' name="soldAmount" onChange={(i)=>handleSoldAmount(i.target.value)}/>
                    </div>
                    <div className='form-group'>
                        <label>Exchange Rate</label>
                        <input className='form-control' name="exchangeRate" onChange={(i)=>handleExchangeRate(i.target.value)}/>
                    </div>
                    <button className='btn btn-primary' onClick={onClickExchange}>Exchange</button>

                </form>
        </div>
    );
}
export default ExchangeComponent;
