import * as React from 'react';
import TextField from '@mui/material/TextField';
import Autocomplete from '@mui/material/Autocomplete';
import Stack from '@mui/material/Stack';
import { useState } from 'react';
function ExchangeC() {
    const accounts = [
        { label: 'Dolar' },
        { label: 'Tl' },
        { label: 'Altın' },
        { label: 'Euro' },

    ];
    const [soldAccountType, setSoldAccountType] = useState();
    const [receivedAccountType, setReceivedAccountType] = useState();
    const [soldAmount, setSoldAmount] = useState();
    const [exchangeRate, setExchangeRate] = useState();

    const handleSoldAccountType = (value) => {
        setSoldAccountType(value);
    }
    const handleReceivedAccountType = (value) => {
        setReceivedAccountType(value);
    }
    const handleSoldAmount = (value) => {
        setSoldAmount(value);
    }
    const handleEx = ()=> {

    if (soldAccountType==="Dolar" && receivedAccountType==="Tl") {
            setExchangeRate(Math.floor(Math.random() * 20) + 15);
    } 
    else if (soldAccountType==="Euro" && receivedAccountType==="Tl") {
        setExchangeRate(Math.floor(Math.random() * 22) + 17);
    }
    else if (soldAccountType==="Altın" && receivedAccountType==="Tl") {
        setExchangeRate(Math.floor(Math.random() * 1000) + 900);
    }
    else if (soldAccountType==="Tl" && receivedAccountType==="Dolar") {
        setExchangeRate(Math.floor(Math.random() * 22) + 17);
    }
    else if (soldAccountType==="Euro" && receivedAccountType==="Dolar") {
        setExchangeRate(Math.floor(Math.random() * 1) + 0.97);
    }
    else if (soldAccountType==="Altın" && receivedAccountType==="Dolar") {
        setExchangeRate(Math.floor(Math.random() * 55) + 50);
    }
    else if (soldAccountType==="Tl" && receivedAccountType==="Euro") {
        setExchangeRate(Math.floor(Math.random() * 0.06) + 0.04);
    }
    else if (soldAccountType==="Dolar" && receivedAccountType==="Euro") {
        setExchangeRate(Math.floor(Math.random() * 1.1) + 0.9);
    }
    else if (soldAccountType==="Altın" && receivedAccountType==="Euro") {
        setExchangeRate(Math.floor(Math.random() * 60) + 50);
    }
    else if (soldAccountType==="Tl" && receivedAccountType==="Altın") {
        setExchangeRate(Math.floor(Math.random() * 0.06) + 0.04);
    }
    else if (soldAccountType==="Dolar" && receivedAccountType==="Altın") {
        setExchangeRate(Math.floor(Math.random() * 1.1) + 0.9);
    }
    else if (soldAccountType==="Euro" && receivedAccountType==="Altın") {
        setExchangeRate(Math.floor(Math.random() * 60) + 50);
    }
}
    return (
        <div className="container">
        <Stack spacing={2} sx={{ width: 300 }}>
            <Autocomplete
                disablePortal
                id="combo-box-demo"
                options={accounts}
                sx={{ width: 300 }}
                renderInput={(params) => <TextField {...params} label="Sold" onChange={(i) => setSoldAccountType(i.target.value)} />}
            />
            <Autocomplete
                disablePortal
                id="combo-box-demo"
                options={accounts}
                sx={{ width: 300 }}
                renderInput={(params) => <TextField {...params} label="Received" onChange={event=>setReceivedAccountType(event.target.value)}/>}
            />
            <TextField onMouseUpCapture={handleEx} id="standard-basic" label="Sold Amount" variant="standard" onChange={event=>setSoldAmount(event.target.value)}/>
            <label>{exchangeRate}</label>
            </Stack>
        </div>
    );
}
export default ExchangeC;
